import React, { Component } from 'react';
import axios from 'axios';


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            weather: [],
            api: [],
            query: ""
        }
        this.fetchWeather = this.fetchWeather.bind(this);
        this.onchange = this.onchange.bind(this);
    }

    onchange = e => {
        this.setState({ query: e.target.value });
        //console.log(this.state.query);
        //this.fetch(query);
        //this.fetchWeather(query);
    };

    fetch = (query) => {
        axios.get(`http://localhost:5000/home/search/${query}`)
        .then( res => this.setState({ api: res.data }) )
    }
    fetchWeather = (query, e) => {
        e.preventDefault()
        var apiKey = 'e7f6b2ade7d4404e80c9d27e0ad3d479';
        axios.get(`http://api.openweathermap.org/data/2.5/forecast?q=${query}&appid=${apiKey}`)
        .then( res => {
            console.log(this.state)
            console.log(res)
            this.setState({ weather: res.data })
        console.log(this.state)
        })
    }

    render() {
        const { query } = this.state;
        const { weather } = this.state;


        const logic = weather.map(a => {
            return(
                <div className="">
                    <h5>{a.city.name}</h5>
                </div>
            )
        })
        return (
            <div>
                <h1>Home Component</h1>
                <form onSubmit={() => this.fetchWeather}>
                    <input name="query" value={query}  onChange={this.onchange}  type="text" />
                    <input type="submit" value="Submit" />
                </form>
                <hr/>
                {logic}
            </div>
        );
    }
}

export default Home;
