import React, { Component } from 'react';
import axios from 'axios';


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            weather: [],
            api: [],
            query: "",
            city:[]
        }
        this.fetchWeather = this.fetchWeather.bind(this);
        this.onchange = this.onchange.bind(this);
    }

    onchange = e => {
        this.setState({ query: e.target.value });
        //console.log(this.state.query);
        //this.fetch(query);
        //this.fetchWeather(this.state.query);
    };
    componentDidMount(){
        let myarg = this.state.myArg;
        this.varifyIt(myarg);
    }
    varifyIt(myarg){
        var apiKey = 'e7f6b2ade7d4404e80c9d27e0ad3d479';


        axios.get(`http://api.openweathermap.org/data/2.5/forecast?q=${myarg}&appid=${apiKey}`)
            .then(res => {
            console.log(res.data.city)
            this.setState({ city: res.data.city })
        })
    }    
    // fetch = (query) => {
    //     axios.get(`http://localhost:5000/home/search/${query}`)
    //     .then( res => this.setState({ api: res.data }) )
    // }
    fetchWeather = (event) => {
        event.preventDefault();
        this.setState({
            myArg: this.state.query
        })
        // const { value } = event
        // var apiKey = 'e7f6b2ade7d4404e80c9d27e0ad3d479';
        // var a = `http://api.openweathermap.org/data/2.5/forecast?q=${value}&appid=${apiKey}`;

        // axios.get(a)
        //     .then(res => {
        //     console.log(res.data.city)
        //     this.setState({ city: res.data.city })
        // })
    }

    render() {
        // const { query } = this.state;
        // const { weather } = this.state;


        // const logic = weather.map(a => {
        //     return(
        //         <div className="">
        //             <h5>{a.city.name}</h5>
        //         </div>
        //     )
        // })
        return (
            <div>
                <h1>Home Component</h1>
                <form onSubmit={e => this.fetchWeather(e)}>
                    <input name="query" value={this.state.query}  onChange={e => this.setState({ query: e.target.value })}  type="text" />
                    <input type="submit" value="Submit" />
                </form>
                <hr/>

            </div>
        );
    }
}

export default Home;
