import React, { Component } from 'react';
import axios from 'axios';


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            weather: [],
            api: [],
            query: "",
            city:[]
        }
        this.fetchWeather = this.fetchWeather.bind(this);
    }
    fetchWeather = (event) => {
        event.preventDefault();
        const { query } = this.state;
        var apiKey = 'e7f6b2ade7d4404e80c9d27e0ad3d479';
        var requestOne =  axios.get(`http://api.openweathermap.org/data/2.5/forecast?q=${query}&appid=${apiKey}`)
        // .then(res => {
        //     console.log(res.data.city)
        //     this.setState({ city: [res.data.city] })
        //     console.log(this.state)
        // });
        var requestTwo =  axios.get(`http://localhost:5000/home/search/${query}`)
        // .then(res => {
        //     console.log(res.data.city)
        //     this.setState({ city: [res.data.city] })
        //     console.log(this.state)
        // })

        axios.all([requestOne, requestTwo])
        .then(axios.spread((acct, perms) =>{
            console.log(perms);

        }))


    }

    render() {
        // const { query } = this.state;
        // const { weather } = this.state;


        // const logic = weather.map(a => {
        //     return(
        //         <div className="">
        //             <h5>{a.city.name}</h5>
        //         </div>
        //     )
        // })
        return (
            <div>
                <h1>Home Component</h1>
                <form onSubmit={e => this.fetchWeather(e)}>
                    <input name="query" value={this.state.query}  onChange={e => this.setState({ query: e.target.value })}  type="text" />
                    <input type="submit" value="Submit" />
                </form>
                <hr/>

            </div>
        );
    }
}

export default Home;
