import React, { Component } from 'react';
import axios from 'axios';


class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            weather: [],

            query: ""
        }
    }

    onchange = e => {
        const query = e.target.value;
        this.setState({ query: query });
        console.log(this.state.query);
        //this.fetch(query);
        this.fetchWeather(query);
    };

    fetch = (query) => {
        axios.get(`http://localhost:5000/home/search/${query}`)
        .then( res => console.log(res))
    }
    fetchWeather = (query) => {
        var apiKey = 'e7f6b2ade7d4404e80c9d27e0ad3d479';
        axios.get(`http://api.openweathermap.org/data/2.5/forecast?q=${query}&appid=${apiKey}`)
        .then( res => this.setState({ weather: res.data }) )
    }

    render() {
        const { query } = this.state;
        return (
            <div>
                <h1>Home Component</h1>
                <input name={query} onChange={this.onchange}  type="text" />
                <hr/>
                {

                }
            </div>
        );
    }
}

export default Home;
